mapper.update:
update order_list
set order_state = '배송중'
where order_no = #{orderNo}

update order_list
set order_state = '반품확정'
where order_no = #{orderNo}

update item
set item_amount = item_amount - #{itemAmount}
where item_no = #{itemNo}

update item
set item_amount = item_amount + #{itemAmount}
where item_no = #{itemNo}
